import shinyswatch
import re
import pandas as pd

from shiny import *

app_ui = ui.page_navbar(
    shinyswatch.theme.superhero(),
    ui.nav(
        "File Upload",
        ui.panel_title("File Upload and Validation"),
        ui.layout_sidebar(
            ui.panel_sidebar(
                ui.input_file("file", "Choose a file to upload:", multiple=True),
            ),
            ui.panel_main(
                ui.h4("Result:"),
                ui.output_text_verbatim("fileStatus"),
            ),
        ),
        ui.h2("Tables"),
        ui.navset_tab(
            ui.nav(
                "Standard Curve Table",
                ui.h4("Standard Curve: "),
                ui.output_table("table1"),
            ),
            ui.nav(
                "Standard Curve Summary Table",
                ui.h4("Standard Curve Summary: "),
                ui.output_table("table2"),
            ),
        ),
    ),
    ui.nav(
        "Standard Curve Check",
        ui.h3("Check Target Genes of SARS"),
        ui.output_text_verbatim("targetcheck"),
        ui.h3("Check Value in Con_std and Ct "),
        ui.output_text_verbatim("valuecheck"),
        ui.h3("Check Minimum 5 diffenrent Con_std and Max 3 False indicaters"),
        ui.output_text_verbatim("numcheck"),
        ui.h3("Check standard deviation between Cts > 0.5:"),
        ui.output_table("sdcheck"),
        ui.h3("Check R^2 < 0.98:"),
        ui.output_table("r2check"),
        ui.h3("Check Efficiency not in 90%-110%:"),
        ui.output_table("effcheck"),
        ui.h3("Check # of SC from â€˜Standard Curveâ€™ tab is the same "),
        ui.output_text_verbatim("sccheck"),
    ),
    title="Check!",
)


def server(input, output, session):
    # Function to validate the correctness of a file name
    def validateFileName(file_name):
        """
        Validate the correctness of a file name.
        Returns True if the file name is valid, False otherwise.
        """
        # File name pattern: starts with a LabId number and end with _raw.xlsx
        pattern = r"^\d.*_raw\.xlsx$"
        return re.match(pattern, file_name) is not None

    @output
    @render.text
    def fileStatus():
        # file_infos is a list of dicts; each dict represents one file. Example:
        # [
        #   {
        #     'name': 'data.csv',
        #     'size': 2601,
        #     'type': 'text/csv',
        #     'datapath': '/tmp/fileupload-1wnx_7c2/tmpga4x9mps/0.csv'
        #   }
        # ]

        file_infos = input.file()
        file_name = file_infos[0]["name"]

        if validateFileName(file_name):
            return "File name is correct."

        # Invalid file name
        else:
            return "Invalid file name. File name should start with a LabID number and end with _raw.xlsx."

    # Function to read standard curve table
    def std_table():
        file_infos = input.file()
        file_name = file_infos[0]["name"]

        if validateFileName(file_name):
            path = input.file()[0]["datapath"]
            # Read excel and set row 7 as header
            raw_std = pd.read_excel(path, 3, 6)
            # keep the wanted rows and columns and remove empty rows
            std = raw_std.iloc[1:, 1:].dropna(subset=["Target"])
        return std

    @output
    @render.table()
    # Dispaly the Excel table
    def table1():
        return std_table()

    def summary_table():
        file_infos = input.file()
        file_name = file_infos[0]["name"]

        if validateFileName(file_name):
            path = input.file()[0]["datapath"]
            # Read excel and set row 3 as header
            raw_summary = pd.read_excel(path, 5, 2)
            # keep the wanted rows and columns and remove empty rows
            summary = raw_summary.iloc[1:, 1:].dropna(subset=["Target"])
        return summary

    @output
    @render.table()
    # Dispaly the Excel table
    def table2():
        return summary_table()

    @output
    @render.text()
    def targetcheck():
        std = std_table()

        # Check if 'N1' or 'N2' is present in the 'Target_Gene(s)' column for SARS
        sars = std[std["Target"] == "SARS-CoV-2"]
        is_n1_n2_present = sars["Target_Gene(s)"].str.contains("N1|N2").any()

        if is_n1_n2_present:
            return "The 'Target_Gene(s)' column of SARS contains 'N1' or 'N2'."
        else:
            return "The 'Target_Gene(s)' column does SARS not contain 'N1' or 'N2'."

    @output
    @render.text()
    def valuecheck():
        std = std_table()
        # Check values are present in Con_std and Ct and is number
        column = [
            "Technical_Replicate_1_CycleNo",
            "Technical_Replicate_2_CycleNo",
            "Technical_Replicate_3_CycleNo",
            "Conc_std (for Standard Curves)",
        ]
        for i in column:
            if std[i].dtype != "float64":
                # Convert to number
                std[i].to_numeric()
                return ("Error:", i, "column is not in number values.")

            elif std[i].isnull().any():
                return ("Error:", i, "column contains empty values.")
            else:
                return "All in numbers and No empty values."

    @output
    @render.text()
    def numcheck():
        # Check Minimum 5 diffenrent Con_std in each Target_Genes for Target, and only up 3 indicater can be FALSE in total 15 points
        ## For Target group not consider different genes
        std = std_table()

        grouped = std.groupby(["Target"])
        for group, group_data in grouped:
            amount = group_data["Conc_std (for Standard Curves)"].nunique()
            indicators_sum = group_data[
                ["Indicator_TR1", "Indicator_TR2", "Indicator_TR3"]
            ].sum(axis=1)
            false_count = (indicators_sum != 3).sum()
            if amount < 5:
                return (group, "less than 5 different Con_std.")
            elif false_count > 3:
                return (group, "greater than 3 False.")
            else:
                return "No Error."

        ## For different genens
        grouped_g = std.groupby(["Target", "Target_Gene(s)"])
        for group, group_data in grouped_g:
            amount = group_data["Conc_std (for Standard Curves)"].nunique()
            indicators_sum = group_data[
                ["Indicator_TR1", "Indicator_TR2", "Indicator_TR3"]
            ].sum(axis=1)
            false_count = (indicators_sum != 3).sum()
            if amount < 5:
                return (group, "less than 5 different Con_std.")
            elif false_count > 3:
                return (group, "greater than 3 False.")
            else:
                return "No Error."

    @output
    @render.table()
    def sdcheck():
        # Check Above 10 gc/ml, sd of Ct between three TR <0.5
        std = std_table()
        sd = std[
            [
                "Technical_Replicate_1_CycleNo",
                "Technical_Replicate_2_CycleNo",
                "Technical_Replicate_3_CycleNo",
            ]
        ].std(axis=1)
        std_copy = std.copy()
        std_copy["sd"] = sd

        filtered_data = std_copy[(std_copy["Conc_std (for Standard Curves)"] > 10)]
        Error = filtered_data[filtered_data["sd"] > 0.5]
        return Error[
            [
                "Target",
                "Target_Gene(s)",
                "Technical_Replicate_1_CycleNo",
                "Technical_Replicate_2_CycleNo",
                "Technical_Replicate_3_CycleNo",
                "Conc_std (for Standard Curves)",
                "sd",
            ]
        ]

    @output
    @render.table()
    def r2check():
        # Check R^2 in the range
        summary = summary_table()
        Error = summary[summary["R2"] <= 0.97]
        return Error[["Target", "Target_Gene(s)", "R2"]]

    @output
    @render.table()
    def effcheck():
        # Check Efficiency in the range 90% -110%
        summary = summary_table()
        Error = summary[
            (summary["Efficiency (%)"] < 90) & (summary["Efficiency (%)"] > 110)
        ]
        return Error[["Target", "Target_Gene(s)", "Efficiency (%)", "Slope"]]

    @output
    @render.text()
    def sccheck():
        std = std_table()
        summary = summary_table()
        # Check # of SC from â€˜Standard Curveâ€™ tab is the same
        if (std["Target"].nunique() == summary["Target"].nunique()) and (
            std["Target_Gene(s)"].nunique() == summary["Target_Gene(s)"].nunique()
        ):
            return "Same number of SC in Standard Curve and Summary"
        else:
            return "Error: Number of SC not same in Standard Curve and Summary!"


app = App(app_ui, server)
